frappe.web_form.on('email', (field, value) => {
    // Get the values of whatsapp_mobile_number and email
    let whatsappMobileNumber = frappe.web_form.get_value('whatsapp_mobile_number');
    let email = frappe.web_form.get_value('email');

    // Concatenate the values and set the result in the 'mobemail' field
    frappe.web_form.set_value('mobemail', String(whatsappMobileNumber) + String(email));
});

