frappe.web_form.validate = () => {
debugger
    // console.log('Validation function running');


    let data = frappe.web_form.get_values();
     if (data != null) {

    // Convert values to integers
    let guestForGalaDinner = parseInt(data.guest_for_gala_dinner, 10);
    let vegFoodChoice = parseInt(data.guest_veg_food_choice, 10);
    let nonVegFoodChoice = parseInt(data.guest_nonveg_food_choice, 10);
    let jainFoodChoice = parseInt(data.guest_jain_food_choice, 10);
    let tot_guestForGalaDinner = vegFoodChoice + nonVegFoodChoice + jainFoodChoice;

    if (guestForGalaDinner !== tot_guestForGalaDinner) {
        frappe.msgprint('Guest food choice should be equal to the number of guests selected for Gala Dinner!!!');
        return false;
    }
    }
};




